  --shadow-sm: 0px 2px 4px -1px hsl(0 0% 0% / 0.18), 0px 1px 2px -1px hsl(0 0% 0% / 0.20);
  --shadow: 0px 4px 6px -1px hsl(0 0% 0% / 0.22), 0px 2px 4px -2px hsl(0 0% 0% / 0.18);
  --shadow-md: 0px 6px 8px -2px hsl(0 0% 0% / 0.24), 0px 4px 6px -2px hsl(0 0% 0% / 0.20);
